		switch (s){
		  case 1:
		  	di(map, x, y);
			map[y - 1][x - 1]--;
			map[y - 1][x + 1]--;
			map[y + 1][x - 1]--;
			map[y + 1][x + 1]--;
			break;

		  case 2:
		  	di(map, x, y);
			break;

		  case 3:
		  	di(map, x, y);
			map[y - 2][x]++;
			map[y][x - 2]++;
			map[y][x + 2]++;
			map[y + 2][x]++;
