int map[14][14];

void put(int x,int y,int size){
	int i,j;
	int d1[2] = {-1,1},d2[2] = {-2,2};

	switch(size){
		case 1:
			for(i = 0;i < 2;i++){
				map[y][x+d1[i]]++;
				map[y+d1[i]][x]++;
			}
			map[y][x]++;
			break;
		case 2:
			for(i = -1;i < 2;i++){
				for(j = -1;j < 2;j++){
					map[y+i][x+j]++;
				}
			}
			break;
		case 3:
			for(i = -1;i < 2;i++){
				for(j = -1;j < 2;j++){
					map[y+i][x+j]++;
				}
			}
			for(i = 0;i < 2;i++){
				map[y][x+d2[i]]++;
				map[y+d2[i]][x]++;
			}
			break;
		default:
			break;
	}
}

