    return 0;
}

void inksim(int x, int y, int s)
{
    int i, j;
    if (loccheck(x, y)) paper[x][y]++;

    for(i = 1; i <= 2; i++){
        if(s == 1 || s == 2)    if (i == 2)  break;

        if (loccheck(x, y + i)) paper[x][y + i]++;
        if (loccheck(x, y - i)) paper[x][y - i]++;
        if (loccheck(x + i, y)) paper[x + i][y]++;
        if (loccheck(x - i, y)) paper[x - i][y]++;
    }

    if (s == 1) return ;

    for(i = -1; i <= 1; i += 2){
        for(j = -1; j <= 1; j += 2){
            if (loccheck(x + i, y + j)) paper[x + i][y + j]++;
        }
    }
    return ;
}

