	max = field[0][0];
	for(i= 0;i<10;i++)
	{
		for(j=0;j<10;j++)
		{
			if(field[i][j] == 0) count++;
			if(max < field[i][j]) max = field[i][j];
		}
	}
	printf("%d\n%d\n",count,max);
	return 0;
}

