	add( x - 1, y - 1 );
	add( x + 1, y - 1 );
	add( x - 1, y + 1 );
	add( x + 1, y + 1 );
}

void bigAdd(int x, int y)
{
	midAdd(x, y);
	add( x    , y - 2 );
