void fillS(int y, int x) {
  int i;
  mas[y][x]++;
  for(i = 0; i < 4; i++) {
    mas[y+dy[i]][x+dx[i]]++;
  }
}

void fillM(int y, int x) {
  int i;
  mas[y][x]++;
  for(i = 0; i < 8; i++) {
    mas[y+dy[i]][x+dx[i]]++;
  }
}

void fillL(int y, int x) {
  int i;
  fillM(y, x);
  for(i = 0; i < 4; i++) {
    mas[y+dy[i]+dy[i]][x+dx[i]+dx[i]]++;
  }
}

#include <stdio.h>

#define FIELDW  ( 10 )
#define FIELDH  ( 10 )
#define ISVALID(X,Y)  ( (X) >= 0 && (Y) >= 0 && (X) < FIELDW && (X) < FIELDH )
int field[ FIELDH ][ FIELDW ] = { { 0 } };

/** Application main entry point. */
int main (
  int argc,
  char *argv[ ]
  )
{
  int res = 0;
  int max = 0;
  int x, y, s;
  int i, j;

  while ( scanf ( " %d,%d,%d", &x, &y, &s ) == 3 )
  {
    ++field[ x ][ y ];

    if ( ISVALID( x, y - 1 ) ) ++field[ x ][ y - 1 ];
    if ( ISVALID( x - 1, y ) ) ++field[ x - 1 ][ y ];
    if ( ISVALID( x + 1, y ) ) ++field[ x + 1 ][ y ];
    if ( ISVALID( x, y + 1 ) ) ++field[ x ][ y + 1 ];

    if ( s < 2 ) continue;

    if ( ISVALID( x - 1, y - 1 ) ) ++field[ x - 1 ][ y - 1 ];
    if ( ISVALID( x + 1, y - 1 ) ) ++field[ x + 1 ][ y - 1 ];
    if ( ISVALID( x - 1, y + 1 ) ) ++field[ x - 1 ][ y + 1 ];
    if ( ISVALID( x + 1, y + 1 ) ) ++field[ x + 1 ][ y + 1 ];

    if ( s < 3 ) continue;

    if ( ISVALID( x, y - 2 ) ) ++field[ x ][ y - 2 ];
    if ( ISVALID( x - 2, y ) ) ++field[ x - 2 ][ y ];
    if ( ISVALID( x + 2, y ) ) ++field[ x + 2 ][ y ];
    if ( ISVALID( x, y + 2 ) ) ++field[ x ][ y + 2 ];
  }

  for ( j = 0; j < FIELDH; ++j )
  for ( i = 0; i < FIELDW; ++i )
  {
    res += !( field[ i ][ j ] );
    if ( field[ i ][ j ] > max )
      max = field[ i ][ j ];
  }

  printf ( "%d\n%d\n", res, max );

  return ( 0 );
}

