	}

	for( i=0; i<H; i++ ){
		for( j=0; j<W; j++ ){
			if( map[i][j] == 0 )
				zero_num++;
			if( map[i][j] > max_dens )
				max_dens = map[i][j];
		}
	}

	printf("%d\n%d\n", zero_num, max_dens );

	return 0;
}

