#include <stdio.h>

enum  {
    MIN_Y = 0,
    MIN_X = 0,
    MAX_Y = 9,
    MAX_X = 9,
    MAX_SIZE = 10,
};

/*  */
static int paper[MAX_SIZE][MAX_SIZE];

static void drop_small(const int, const int);
static void drop_mid(const int, const int);
static void drop_big(const int, const int);


int main(void) {
    int i, j, x, y, size, cnt, deep = 0;

    while (EOF != scanf("%d %d %d", &x, &y, &size)) {
        switch (size) {
            case 1:
                drop_small(x, y);
                break;
            case 2:
                drop_mid(x, y);
                break;
            case 3:
                drop_big(x, y);
                break;
        }
    }

    for (i = MIN_Y; i <= MAX_Y; i++) {
        for (j = MIN_X; j <= MAX_X; j++) {
            if (paper[i][j] == 0) {
                ++cnt;
            } else if (deep < paper[i][j]) {
                deep = paper[i][j];
            }
        }
    }

    printf("%d\n%d", cnt, deep);

    return 0;
}


void drop_small(const int x, const int y) {
    ++paper[x][y];

    /*  */
    if (MIN_X < x) {
        ++paper[x - 1][y];
    }

    /*  */
    if (x < MAX_X) {
        ++paper[x + 1][y];
    }

    /*  */
    if (MIN_Y < y) {
        ++paper[x][y - 1];
    }

    /*  */
    if (y < MAX_Y) {
        ++paper[x][y - 1];
    }
}


void drop_mid(const int x, const int y) {
    drop_small(x, y);

    /*  */
    if (MIN_X < x && MIN_Y < y) {
        ++paper[x - 1][y - 1];
    }

    /*  */
    if (x < MAX_X && MIN_Y < y) {
        ++paper[x + 1][y - 1];
    }

    /*  */
    if (MIN_X < x && y < MAX_Y) {
        ++paper[x - 1][y + 1];
    }

    /*  */
    if (x < MAX_X && y < MAX_X) {
        ++paper[x + 1][y + 1];
    }
}


void drop_big(const int x, const int y) {
    drop_mid(x, y);

    /*  */
    if (MIN_X + 1 < x) {
        ++paper[x - 2][y];
    }

    /*  */
    if (x < MAX_X - 1) {
        ++paper[x + 2][y];
    }

    /*  */
    if (y < MIN_Y + 1) {
        ++paper[x][y - 2];
    }

    /*  */
    if (y < MAX_Y - 1) {
        ++paper[x][y + 2];
    }
}

