        while(scanf("%d,%d,%d", &x, &y, &size) != EOF)
        {
                if(size == L)
                {
                        large(x, y, ppr);
                }
                if(size == M)
                {
                        middle(x, y, ppr);
                }
                if(size == S)
                {
                        small(x, y, ppr);
                }
        }
        for(i = 0; i < X; i++)
        {
                for(j = 0;j < Y; j++)
                {
