#define REP(i,a,b) for(i=a;i<b;i++)
#define rep(i,n) REP(i,0,n)

int main(){
  int i,j,x,y,size,mx,my,res=0,depth=0;
  int map[10][10];
  int dx[] = {0,1,0,-1}, dy[] = {1,0,-1,0};

  memset(map,0,sizeof(map));

  while(scanf("%d,%d,%d\n",&x,&y,&size) != EOF){
    if(size == 1){
      map[x][y]++;
      rep(i,4){
        mx = x + dx[i]; my = y + dy[i];
        if(mx < 10 && my < 10 && 0 <= mx && 0 <= my) map[mx][my]++;
      }
    }

    if(size == 2){
      REP(i,-1,2) REP(j,-1,2){
        mx = x + i; my = y + j;
        if(mx < 10 && my < 10 && 0 <= mx && 0 <= my) map[mx][my]++;
      }
    }

    if(size == 3){
      REP(i,-1,2) REP(j,-1,2){
        mx = x + i; my = y + j;
        if(mx < 10 && my < 10 && 0 <= mx && 0 <= my) map[mx][my]++;
      }
      rep(i,4){
        mx = x + 2*dx[i]; my = y + 2*dy[i];
        if(mx < 10 && my < 10 && 0 <= mx && 0 <= my) map[mx][my]++;
      }
    }
  }

  rep(i,10) rep(j,10){
      if(map[i][j] != 0 && depth < map[i][j]) depth = map[i][j];
      if(map[i][j] == 0) res++;
  }

  printf("%d\n%d\n",res,depth);

  return 0;
}

