#include<stdio.h>

void littleDrop(int,int,int *,int);
void midiumDrop(int,int *,int,int);
void largeDrop(int,int *,int);
void showArray(int [][10]);

void largeDrop(int center,int *tbl,int place){
	if(center >= 20)//
		*(tbl - 20) += 1;
	if(place >= 2)//
		*(tbl - 2) += 1;
	if(place <= 7)//
		*(tbl + 2) += 1;
	if(center <= 80)//
		*(tbl + 20) += 1;
}

void midiumDrop(int center,int *tbl ,int size, int place){
//+1//
	if(center > 10){//
		if(place != 0)//
			*(tbl - 11) += 1;
		if(place != 9)//
			*(tbl - 9) += 1;
	}
	if(center < 90){//
		if(place != 0)//
			*(tbl + 9) += 1;
		if(place != 9)//
			*(tbl + 11) += 1;
	}
	if(size == 3)
		largeDrop(center,tbl,place);
}

void littleDrop(int x ,int y ,int *tbl,int size){
//(0,0)
	int center = x + y * 10;
	int place = center % 10;//
//(0,0)
	tbl = (tbl + center);

//+1
	*(tbl) += 1;
	if(center >= 10)//center
		*(tbl - 10) += 1;
	if(center <= 90)//center
		*(tbl + 10) += 1;
	if(place != 0)//center
		*(tbl - 1) += 1;
	if(place != 9)//center
		*(tbl + 1) += 1;
	if(size >= 2)
		midiumDrop(center, tbl, size,place);
}

