#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define FIELDW  ( 10 )
#define FIELDH  ( 10 )
#define ISVALID(X,Y)  ( (X) >= 0 && (Y) >= 0 && (X) < FIELDW && (Y) < FIELDH )
int field[ FIELDH ][ FIELDW ] = { { 0 } };

/** Application main entry point. */
int main (
  int argc,
  char *argv[ ]
  )
{
  int res = 0;
  int max = 0;
  int x, y, s;
  int i, j;

  while ( scanf ( " %d,%d,%d", &x, &y, &s ) == 3 )
  {
    ++field[ y ][ x ];

    if ( ISVALID( x, y - 1 ) ) ++field[ y - 1 ][ x ];
    if ( ISVALID( x - 1, y ) ) ++field[ y ][ x - 1 ];
    if ( ISVALID( x + 1, y ) ) ++field[ y ][ x + 1 ];
    if ( ISVALID( x, y + 1 ) ) ++field[ y + 1 ][ x ];

    if ( s < 2 ) continue;

    if ( ISVALID( x - 1, y - 1 ) ) ++field[ y - 1 ][ x - 1 ];
    if ( ISVALID( x + 1, y - 1 ) ) ++field[ y - 1 ][ x + 1 ];
    if ( ISVALID( x - 1, y + 1 ) ) ++field[ y + 1 ][ x - 1 ];
    if ( ISVALID( x + 1, y + 1 ) ) ++field[ y + 1 ][ x + 1 ];

    if ( s < 3 ) continue;

    if ( ISVALID( x, y - 2 ) ) ++field[ y - 2 ][ x ];
    if ( ISVALID( x - 2, y ) ) ++field[ y ][ x - 2 ];
    if ( ISVALID( x + 2, y ) ) ++field[ y ][ x + 2 ];
    if ( ISVALID( x, y + 2 ) ) ++field[ y + 2 ][ x ];
  }

  for ( j = 0; j < FIELDH; ++j )
  for ( i = 0; i < FIELDW; ++i )
  {
    res += !( field[ j ][ i ] );
    if ( field[ j ][ i ] > max )
      max = field[ j ][ i ];
  }

  for ( j = 0; j < FIELDH; ++j )
  {
    for ( i = 0; i < FIELDW; ++i )
    {
      printf ( "%2d", field[ j ][ i ] );
    }
    printf ( "\n" );
  }

  printf ( "%d\n%d\n", res, max );

  return ( 0 );
}

