void fillS(int y, int x) {
  int i;
  mas[y][x]++;
  for(i = 0; i < 4; i++) {
    if(in(y+dy[i], x+dx[i])) {
      mas[y+dy[i]][x+dx[i]]++;
    }
  }
}

void fillM(int y, int x) {
  int i;
  mas[y][x]++;
  for(i = 0; i < 8; i++) {
    if(in(y+dy[i], x+dx[i])) {
      mas[y+dy[i]][x+dx[i]]++;
    }
  }
}

void fillL(int y, int x) {
  int i;
  fillM(y, x);
  for(i = 0; i < 4; i++) {
    if(in(y+dy[i]+dy[i], x+dx[i]+dx[i])) {
      mas[y+dy[i]+dy[i]][x+dx[i]+dx[i]]++;
    }
  }
}

