void smallInk(int Field[][10],int ink[]){
	int x,y;										/*ix,jy*/
	for(x=ink[0]-1;x<=ink[0]+1;x++){
		if(x==ink[0]){								/*^*/
			for(y=ink[1]-1;y<=ink[1]+1;y++){
				if(0<=y && y<10){						/*0<=x<9AiwB*/
					Field[y][x]++;
				}
			}
		}else{										/**/
			if(0<=x && x<10){
				Field[ ink[1] ][x]++;
			}
		}
	}
}

void regularInk(int Field[][10],int ink[]){
	int x,y;
	for(x=ink[0]-1;x<=ink[0]+1;x++){
		for(y=ink[1]-1;y<=ink[1]+1;y++){
			if( (0<=x && x<10) && (0<=y && y<10)){
				Field[y][x]++;
			}
		}
	}
}

void largeInk(int Field[][10],int ink[]){
	int x,y;
	for(x=ink[0]-2;x<=ink[0]+2;x++){
		/*j,ink[1]*/
		if(x==ink[0]-2 || x==ink[0]+2){		/*1*/
			if(0<=x && x<10){
				Field[ ink[1] ][x]++;
			}
		}else if(x==ink[0]-1 || x==ink[0]+1){	/*3*/
			for(y=ink[1]-1;y<=ink[1]+1;y++){
				if( (0<=x && x<10) && (0<=y && y<10)){
					Field[y][x]++;
				}
			}
		}else if(x==ink[0]){						/*5*/
			for(y=ink[1]-2;y<=ink[1]+2;y++){
				if( (0<=x && x<10) && (0<=y && y<10)){
					Field[y][x]++;
				}
			}
		}
	}
}

