		for(j = 0; j < 10; j ++){
			if(tbl[i][j] == 0)
				clear ++;
			if(tbl[i][j] > max)
				max = tbl[i][j];
		}
	}
	printf("%d\n",clear);
	printf("%d\n",max);
}

