void large(int x, int y, int ppr[X][Y])
{
        ppr[x - 2][y]     += 1;
        ppr[x + 2][y]     += 1;
        ppr[x]    [y - 2] += 1;
        ppr[x]    [y + 2] += 1;
        ppr[x - 1][y - 1] += 1;
        ppr[x - 1][y]     += 1;
        ppr[x - 1][y + 1] += 1;
        ppr[x]    [y - 1] += 1;
        ppr[x]    [y]     += 1;
        ppr[x]    [y + 1] += 1;
        ppr[x + 1][y + 1] += 1;
        ppr[x + 1][y]     += 1;
        ppr[x + 1][y - 1] += 1;
}

void middle(int x, int y, int ppr[X][Y])
{
        ppr[x - 1][y-1]   += 1;
        ppr[x - 1][y]     += 1;
        ppr[x - 1][y+1]   += 1;
        ppr[x]    [y-1]   += 1;
        ppr[x]    [y]     += 1;
        ppr[x]    [y+1]   += 1;
        ppr[x + 1][y + 1] += 1;
        ppr[x + 1][y]     += 1;
        ppr[x + 1][y - 1] += 1;
}
void small(int x, int y, int ppr[X][Y])
{
        ppr[x + 1][y]     += 1;
        ppr[x]    [y]     += 1;
        ppr[x - 1][y]     += 1;
        ppr[x]    [y + 1] += 1;
        ppr[x]    [y - 1] += 1;
}

