void largeDrop(int center,int *tbl){
	if(center >= 20)
		*(tbl - 20) += 1;
	if(center % 10 >= 2)
		*(tbl - 2) += 1;
	if(center % 10 <= 7)
		*(tbl + 2) += 1;
	if(center <= 80)
		*(tbl + 20) += 1;
}

void midiumDrop(int center,int *tbl ,int size){
//+1//
	if(center % 10 != 0)
		*(tbl - 11) += 1;
	if(center % 10 != 9)
		*(tbl - 9) += 1;
	if(center % 10 != 0)
		*(tbl + 9) += 1;
	if(center % 10 != 9) 
		*(tbl + 11) += 1;
	if(size == 3)
		largeDrop(center,tbl);
}


void littleDrop(int x ,int y ,int *tbl,int size){
//(0,0)
	int center = x + y * 10;
//(0,0)
	tbl = (tbl + center);

//+1
	*(tbl) += 1;
//+1
	if(center >= 10)
		*(tbl - 10) += 1;
	if(center <= 90)
		*(tbl + 10) += 1;
	if(center % 10 != 0)
		*(tbl - 1) += 1;
	if(center % 10 != 9)
		*(tbl + 1) += 1;
	if(size >= 2)
		midiumDrop(center, tbl, size);
}



