void drop(int x,int y,int s){
	int i,l;

	for(i=x-1;i<=x+1;i++){
		if(i<0 || i>9)continue;
		for(l=y-1;l<=y+1;l++){
			if(l<0 || l>9)continue;
			if(s==1 && (i!=x && l!=y))continue;
			m[i][l]++;
		}
	}
	if(s==3){
		i=x-2;
		if(i>=0)m[i][y]++;
		i=x+2;
		if(i<=9)m[i][y]++;
		l=y-2;
		if(l>=0)m[x][l]++;
		l=y+2;
		if(l<=9)m[x][l]++;
	}
	return;
}

