#include <stdio.h>

//#define DEBUG

#define LEN 10
int paper[LEN][LEN];

void init(void)
{
    int i,j;
    for(i=0;i<LEN;i++){
        for(j=0;j<LEN;j++){
            paper[i][j]=0;
        }
    }
}

//increse a certain point
void set(int x,int y)
{
    if(x<0 || y<0 || x>=LEN || y>=LEN){ return; } 
    paper[x][y]++;
}

//drop a ink
void drop_1(int x,int y)
{
    set(x,y);
    set(x+1,y);
    set(x-1,y);
    set(x,y+1);
    set(x,y-1);
}
void drop_2(int x,int y)
{
    drop_1(x,y);
    set(x+1,y+1);
    set(x+1,y-1);
    set(x-1,y+1);
    set(x-1,y-1);
}
void drop_3(int x,int y)
{
    drop_2(x,y);
    set(x+2,y);
    set(x-2,y);
    set(x,y+2);
    set(x,y-2);
}

void update(int x,int y,int size)
{
    if(size==1){ drop_1(x,y); }
    else if(size==2){ drop_2(x,y); }
    else if(size==3){ drop_3(x,y); }
}

void result(void)
{
    int cnt_no_ink=0,max_ink=0;
    int i,j;
    for(i=0;i<LEN;i++){
        for(j=0;j<LEN;j++){
            if(paper[i][j]==0){
                cnt_no_ink++;
            }
            if(paper[i][j]>max_ink){
                max_ink=paper[i][j];
            }
        }
    }
    printf("%d\n%d\n",cnt_no_ink,max_ink);
}



int main(void)
{
    FILE* fp;
#ifdef DEBUG
    fp=fopen("input.txt","r");
#else
    fp=stdin;
#endif
    init(); //init paper
    while(feof(fp)==0){
        int x,y,size;
        fscanf(fp,"%d,%d,%d\n",&x,&y,&size);
        update(x,y,size);
    }
    result(); //print result
    return 0;
}

