void drop(int paper[][10], int x, int y, int size)
{
	if (x + size < 10){
		paper[x + size][y]++;
	}
	if (x - size >= 0){
		paper[x - size][y]++;
	}
	if (y + size < 10){
		paper[x][y + size]++;
	}
	if (y - size >= 0){
		paper[x][y - size]++;
	}
}

