				paper[x + 1][y + 1]++;
			}
			if (x - 1 >= 0 && y + 1 < 10){
				paper[x - 1][y + 1]++;
			}
			if (x + 1 < 10 && y - 1 >= 0){
				paper[x + 1][y - 1]++;
			}
			if (x - 1 >= 0 && y - 1 >= 0){
				paper[x - 1][y - 1]++;
			}
		}
