#include <stdio.h>
#define SIZE 10
struct Ink{
	int x, y;
	int size;
};

void fill(int x, int y, int paper[SIZE][SIZE]){
	if(x < 0 || x >= SIZE){return;}
      	if(y < 0 || y >= SIZE){return;}
	paper[x][y]++;	
}

void drop(struct Ink ink, int paper[SIZE][SIZE]){
	int i,j;
	switch(ink.size){
		case 1:
			fill(ink.x  , ink.y  , paper);
			fill(ink.x  , ink.y-1, paper);
			fill(ink.x-1, ink.y  , paper);
			fill(ink.x  , ink.y+1, paper);
			fill(ink.x+1, ink.y  , paper);
		break;
		case 2:
			for(i = -1; i <= 1; i++){
				for(j = -1; j <= 1; j++){
					fill(ink.x + i, ink.y + j, paper);
				}
			}
		break;
		case 3:	
			fill(ink.x  , ink.y-2, paper);
			fill(ink.x-2, ink.y  , paper);
			fill(ink.x  , ink.y+2, paper);
			fill(ink.x+2, ink.y  , paper);
			for(i = -1; i <= 1; i++){
				for(j = -1; j <= 1; j++){
					fill(ink.x + i, ink.y + j, paper);
				}
			}
		break;
	}	
}

void printResult(int paper[SIZE][SIZE]){
	int i,j;
	int bCounter = 0;
	int max = 0;
	for(i = 0; i < SIZE; i++){
		for(j = 0; j < SIZE; j++){
			if(!paper[i][j]){
				bCounter++;
			}else if(paper[i][j] > max){
				max = paper[i][j];
			}
		}
	}
	printf("%d\n%d\n", bCounter, max);
}

int main(void){
	char str[6];
	int paper[SIZE][SIZE];
	int i,j;
	struct Ink ink;
	for(i = 0; i < SIZE; i++){
		for(j = 0; j < SIZE; j++){
			paper[i][j] = 0;
		}
	}
	while(1){
		if( scanf("%s",str) == EOF){break;}
		ink.x = str[0] - '0';
		ink.y = str[2] - '0';
		ink.size = str[4] - '0';
		drop(ink,paper);
	}
	printResult(paper);
	return 0;
}

