int cell[14][14] = {0};
void small(int x, int y)
{
  cell[x][y - 1]++;
  cell[x - 1][y]++;
  cell[x + 1][y]++;
  cell[x][y + 1]++;
}

void medium(int x, int y)
{
  int i, j;
  x--;
  y--;
  for (i = 0; i < 3; i++){
    for (j = 0; j < 3; j++){
      if (i != 1 && j != 1){
        cell[x + j][y + i]++;
      }
    }
  }
}

void big(int x, int y)
{
  int i, j;
  cell[x][y - 2]++;
  cell[x - 2][y]++;
  cell[x + 2][y]++;
  cell[x][y + 2]++;
  x--;
	y--;
  for (i = 0; i < 3; i++){
    for (j = 0; j < 3; j++){
      if (i != 1 && j != 1){
        cell[x + j][y + i]++;
      }
    }
  }
}

