#define N 10

void DropInk(int, int);

int data[N][N];

int main(){
  int i, j, x, y, size, count=0, max=0;
  char c1, c2;

  for(i=0; i<N; i++){
    for(j=0; j<N; j++){
      data[i][j] = 0;
    }
  }

  while(scanf("%d %c %d %c %d", &x, &c1, &y, &c2, &size) != EOF){
    if(size == 1){
      DropInk(y-1, x);
      DropInk(y, x-1);
      DropInk(y, x);
      DropInk(y, x+1);
      DropInk(y+1, x);
    }else if(size == 2){
      DropInk(y-1, x-1);
      DropInk(y-1, x);
      DropInk(y-1, x+1);
      DropInk(y, x-1);
      DropInk(y, x);
      DropInk(y, x+1);
      DropInk(y+1, x-1);
      DropInk(y+1, x);
      DropInk(y+1, x+1);
    }else if(size == 3){
      DropInk(y-2, x);
      DropInk(y-1, x-1);
      DropInk(y-1, x);
      DropInk(y-1, x+1);
      DropInk(y, x-2);
      DropInk(y, x-1);
      DropInk(y, x);
      DropInk(y, x+1);
      DropInk(y, x+2);
      DropInk(y+1, x-1);
      DropInk(y+1, x);
      DropInk(y+1, x+1);
      DropInk(y+2, x);
    }
  }

  for(i=0; i<N; i++){
    for(j=0; j<N; j++){
      if(data[i][j] == 0)
         count++;
      else if(data[i][j] > max)
         max = data[i][j];
    }
  }

  printf("%d\n%d\n", count, max);
  return 0;
}

void DropInk(int i, int j){
  if(i>-1 && i<N && j>-1 && j<N)
    data[i][j]++;
}

