void small(int a[][10],int x,int y){


  a[x][y]++;
  if(y>0){
    a[x][y-1]++;
  }
  if(y<9){
    a[x][y+1]++;
  }
  if(x<9){
    a[x+1][y]++;
  }
  if(x>0){
    a[x-1][y]++;
  }
  return;
}

void mid(int a[][10],int x,int y){

  a[y][x]++;
  if(x>0){
    a[y][x-1]++;
    if(x>0&&y>0){
      a[y-1][x-1]++;
    }
    if(x>0&&y<9){
      a[y+1][x-1]++;
    }
  }
  if(x<9){
    a[y][x+1]++;
    if(x<9&&y>0){
      a[y-1][x+1]++;
    }
    if(x<9&&y<9){
      a[y+1][x+1]++;
    }
  }
  if(y<9){
    a[y+1][x]++;
  }
  if(y>0){
    a[y-1][x]++;
  }
  return;
}

void large(int a[][10],int x,int y){

  a[y][x]++;
  if(x>0){
    a[y][x-1]++;
    if(x>0&&y>0){
      a[y-1][x-1]++;
    }
    if(x>0&&y<9){
      a[y+1][x-1]++;
    }
    if(x>1){
      a[y][x-2]++; 
    }
  }
  if(x<9){
    a[y][x+1]++;
    if(x<9&&y>0){
      a[y-1][x+1]++;
    }
    if(x<9&&y<9){
      a[y+1][x+1]++;
    }
    if(x<8){
      a[y][x+2]++; 
    }
  }
  if(y<9){
    a[y+1][x]++;
    if(x<8){
      a[y+2][x]++; 
    }
  }
  if(y>0){
