int check(int n)
{
	if(n >= 0 && n <= 10) return 1;
	else return 0;
}

void point(int p[][10],int x,int y,int size)
{
	int i,j;

	if(size == 1)
	{
		p[y][x]++;
		if(check(x-1)) p[y][x-1]++;
		if(check(x+1)) p[y][x+1]++;
		if(check(y-1)) p[y-1][x]++;
		if(check(y+1)) p[y+1][x]++;
	}
	if(size == 2)
	{
		for(i = x-1;i<=x+1;i++)
		{
			for(j = y-1;j <= y+1;j++)
			{
				if(check(i) && check(j)) p[j][i]++;
			}
		}
	}
	if(size == 3)
	{
		for(i = x-1;i<=x+1;i++)
		{
			for(j = y-1;j <= y+1;j++)
			{
				if(check(i) && check(j)) p[j][i]++;
			}
		}
		if(check(x-2)) p[y][x-2]++;
		if(check(x+2)) p[y][x+2]++;
		if(check(y-2)) p[y-2][x]++;
		if(check(y+2)) p[y+2][x]++;
	}
}


