void largeDrop(int center,int *tbl){
	if(center >= 20)
		*(tbl - 20) += 1;
	if(center % 10 >= 2)
		*(tbl - 2) += 1;
	if(center % 10 <= 7)
		*(tbl + 2) += 1;
	if(center <= 80)
		*(tbl + 20) += 1;
}

void midiumDrop(int center,int *tbl ,int size){
//+1//
	if(center % 10 != 0)
		*(tbl - 11) += 1;
	if(center % 10 != 9)
		*(tbl - 9) += 1;
	if(center % 10 != 0)
		*(tbl + 9) += 1;
	if(center % 10 != 9) 
		*(tbl + 11) += 1;
	if(size == 3)
		largeDrop(center,tbl);
}


