	if(center <= 90)
		*(tbl + 10) += 1;
	if(center % 10 != 0)
		*(tbl - 1) += 1;
	if(center % 10 != 9)
		*(tbl + 1) += 1;
	if(size >= 2)
		midiumDrop(center, tbl, size);
}

