		switch(size){
		case 3:
			drop(x+2,y);
			drop(x,y+2);
			drop(x-2,y);
			drop(x,y-2);
		case 2:
			drop(x+1,y+1);
			drop(x+1,y-1);
			drop(x-1,y+1);
			drop(x-1,y-1);
		case 1:
			drop(x+1,y);
			drop(x,y+1);
			drop(x-1,y);
			drop(x,y-1);
		default:
			drop(x,y);
		}
	}

	x=0;y=0;
	for(i=0;i<10;i++){
		for(j=0;j<10;j++){
			if(p[i][j]==0)x++;
			else if(p[i][j]>y)y=p[i][j];
		}
	}

	printf("%d\n%d\n",x,y);
	return 0;
