			ARR[x  +xofs][y-2+xofs]++;
		}

	}

	max=0;
	empty=0;
	for(i=0;i<10;i++){
		for(j=0;j<10;j++){
		if(ARR[i+xofs][j+xofs]==0)empty++;
		if(ARR[i+xofs][j+xofs]>=max)max=ARR[i+xofs][y+xofs];
		}
	}
	printf("%d\n%d\n",empty,max);

