void inkpaint1(int x, int y)
{
    map[x][y] += 1;
    map[x - 1][y] += 1;
    map[x + 1][y] += 1;
    map[x][y + 1] += 1;
    map[x][y - 1] += 1;
}
void inkpaint2(int x, int y)
{
    inkpaint1(x, y);
    map[x - 1][y - 1] += 1;
    map[x - 1][y + 1] += 1;
    map[x + 1][y - 1] += 1;
    map[x + 1][y + 1] += 1;
}
void inkpaint3(int x, int y)
{
    inkpaint2(x, y);
    map[x][y - 2] += 1;
    map[x][y + 2] += 1;
