		printf("call fat_man\n");
#endif
		fat_man(map, input);
	}
}
void tnt(int map[][SIZE], int input[])
{
	short i = 0, j = 0;
	for(i = input[0] - 1; i <= input[0] + 1; i++)
	{
		if((i < 0) || (i > 10))
		{
			continue;
		}
		else
		{
			map[input[1]][i]++;
		}
#if compile == 4
		printf("map[%d][%d] = %d\n", input[1], i, map[input[1]][i]);
#endif
	}
	for(i = input[1] - 1; i <= input[1] + 1; i++)
	{
		if((i < 0) || (i > 10))
		{
			continue;
		}
		else
		{
			map[i][input[0]]++;
		}
#if compile == 4
		printf("map[%d][%d] = %d\n", i, input[0], map[i][input[0]]);
#endif
	}
	map[input[1]][input[0]]--;
}
void little_boy(int map[][SIZE], int input[])
{
	short i = 0, j = 0;
	for(i = input[1] - 1; i <= input[1] + 1; i++)
	{
		for(j = input[0] - 1; j <= input[0] + 1; j++)
		{
			if((i < 0) || (j < 0) || (i > 10) || (j > 10))
			{
				continue;
			}
			else
			{
				map[i][j]++;
			}
		}
	}
}
void fat_man(int map[][SIZE], int input[])
{
	short i = 0, j = 0;
	for(i = input[1] - 1; i <= input[1] + 1; i++)
	{
		for(j = input[0] - 1; j <= input[0] + 1; j++)
		{
			if((i < 0) || (j < 0) || (i > 10) || (j > 10))
			{
				continue;
			}
			else
			{
				map[i][j]++;
			}
		}
	}
	if((input[0] - 2) >= 0)
	{
		map[input[1]][input[0] - 2]++;
	}
	if((input[0] + 2) <= 9)
	{
		map[input[1]][input[0] + 2]++;
	}
	if((input[1] - 2) >= 0)
	{
		map[input[1] - 2][input[0]]++;
	}
	if((input[1] + 2) <= 9)
	{
		map[input[1] + 2][input[0]]++;
	}
}
void check_result(int map[][SIZE], int *white,int *max)
{
	short i = 0, j = 0;
	*max = map[0][0];
	for(i = 0; i < SIZE; i++)
	{
		for(j = 0; j < SIZE; j++)
		{
			if(map[i][j] > *max)
			{
				*max = map[i][j];
			}
			if(map[i][j] == 0)
			{
				*white += 1;
			}
		}
	}
}

