int main(void)
{
	int x, y, s,i,j,k,max=0,white=0;
	int fild[10][10] = {0};

	while (scanf("%d,%d,%d", &x, &y, &s) != EOF) {
		if (fild[x][y] == 0)white++;
		if (s == 1) {
			for (i = x - 1; i < x + 2; i++) {
				if (i >= 0 && i < 10) {
					if (i != x && fild[i][y] == 0)white++;
					fild[i][y] += 1;
				}
			}
			for (i = y - 1; i < y + 2; i++) {
				if (i >= 0 && i < 10) {
					if (i != y && fild[x][i] == 0)white++;
					fild[x][i] += 1;
				}
			}
		} 
		else if (s == 2) {
			for (i = x - 1; i < x + 2; i++) {
				for (j = y - 1; j < y + 2; j++) {
					if (i >= 0 && i < 10 && j >= 0 && j < 10) {
						if ((i != x || j != y) && fild[i][j] == 0)white++;
						fild[i][j] += 1;
					}
				}
			}
		}
		else if (s == 3) {
			k = 0;
			for (j = y - 2 + k; j <= y - 2 + k; j++) {
				for (i = x - k; i <= x + k; i++) {
					if (i >= 0 && i < 10 && j >= 0 && j < 10) {
						if ((i != x || j != y) && fild[i][j] == 0)white++;

						fild[i][j] += 1;
					}
				}
				k++;
				if (k == 3)break;
			}
			k = 0;
			for (j = y + 1 + k; j <= y + 1 + k; j++) {
				for (i = x -1 + k; i <= x+1-k; i++) {
					if (i >= 0 && i < 10 && j >= 0 && j < 10) {
						if (fild[i][j] == 0)white++;
						fild[i][j] += 1;
					}
				}
				k++;
				if (k == 2)break;
			}
		}
	}
	for (i = 0; i < 10; i++) {
		for (j = 0; j < 10; j++) {
			if (max < fild[i][j])
				max = fild[i][j];
		}
	}
	printf("%d\n", 100-white);
	printf("%d\n", max);

	return 0;
}

