#include <stdio.h>

void color(int map[10][10], int x, int y)
{
  if (0 <= x && x <= 9 && 0 <= y && y <= 9){
    map[y][x]++;
  }
}

int main(void)
{
  int map[10][10] = {0};
  int x, y, size;
  int i, j;
  int white = 0, darkest = 0;

  while (scanf("%d,%d,%d", &x, &y, &size) != EOF){
    map[y][x]++;
    if (size == 3){
      color(map, x + 2, y);
      color(map, x - 2, y);
      color(map, x, y + 2);
      color(map, x, y - 2);
    }
    if (size >= 2){
      color(map, x + 1, y + 1);
      color(map, x - 1, y + 1);
      color(map, x + 1, y - 1);
      color(map, x - 1, y - 1);
    }
    if (size >= 1){
      color(map, x, y + 1);
      color(map, x, y - 1);
      color(map, x + 1, y);
      color(map, x - 1, y);
    }
  }

  for (i = 0; i < 10; i++){
    for (j = 0; j < 10; j++){
      if (map[i][j] == 0){
        white++;
      }
      if (darkest < map[i][j]){
        darkest = map[i][j];
      }
    }
  }

  printf("%d\n%d\n", white, darkest);

  return (0);
}

