      for(j = 0 ; j < 2 ; j++){
	if(x+size1[i] >= 0 && x+size1[i] < 10 && y+size1[j] >= 0 && y+size1[j] < 10)
	  a[y+size1[i]][x+size1[j]] += 1;
      }
    }
