#include<stdio.h>

void littleDrop(int,int,int *,int);
void midiumDrop(int,int *,int,int);
void largeDrop(int,int *,int);
void showArray(int [][10]);

void largeDrop(int center,int *tbl ,int round){
	if(center >= 20)//
		*(tbl - 20) += 1;
	if(round >= 2)//
		*(tbl - 2) += 1;
	if(round <= 7)//
		*(tbl + 2) += 1;
	if(center <= 80)//
		*(tbl + 20) += 1;
}

void midiumDrop(int center,int *tbl ,int size ,int round){
//+1//
	if(center > 10){
		if(round != 0)//
			*(tbl - 11) += 1;
		if(round != 9)//
			*(tbl - 9) += 1;
	if(center < 90){
		if(round != 0)//
			*(tbl + 9) += 1;
		if(round != 9)//
			*(tbl + 11) += 1;
	}
	if(size == 3)
		largeDrop(center,tbl,round);
}


void littleDrop(int x ,int y ,int *tbl,int size){
//(0,0)
	int center = x + y * 10;
	int round = center % 10;
//(0,0)
	tbl = (tbl + center);

//+1
	*(tbl) += 1;
//+1
	if(center >= 10)//
		*(tbl - 10) += 1;
	if(center <= 90)//
		*(tbl + 10) += 1;
	if(round != 0)//
		*(tbl - 1) += 1;
	if(round != 9)//
		*(tbl + 1) += 1;
	if(size >= 2)
		midiumDrop(center, tbl, size,round);
}

