	a[y][x+size1[i]] += 1;
      if(y+size1[i] >= 0 || y+size1[i] <= 10)
	a[y+size1[i]][x] += 1;
    }
  }
  if(size == 2){
    for(i = 0 ; i < 2 ; i++){
      for(j = 0 ; j < 2 ; j++){
	if(x+size1[i] >= 0 || x+size1[i] <= 10 || y+size1[i] >= 0 || y+size1[i] <= 10)
	  a[y+size1[i]][x+size1[j]] += 1;
      }
    }
    for(i = 0 ; i < 2 ; i++){
      if(x+size1[i] >= 0 || x+size1[i] <= 10)
	a[y][x+size1[i]] += 1;
      if(y+size1[i] >= 0 || y+size1[i] <= 10)
	a[y+size1[i]][x] += 1;
    }
  }
  if(size == 3){
    for(i = 0 ; i < 2 ; i++){
      for(j = 0 ; j < 2 ; j++){
	if(x+size1[i] >= 0 || x+size1[i] <= 10 || y+size1[i] >= 0 || y+size1[i] <= 10)
	  a[y+size1[i]][x+size1[j]] += 1;
      }
    }
    for(i = 0 ; i < 2 ; i++){
      if(x+size1[i] >= 0 || x+size1[i] <= 10)
	a[y][x+size1[i]] += 1;
      if(y+size1[i] >= 0 || y+size1[i] <= 10)
	a[y+size1[i]][x] += 1;
    }
    for(i = 0 ; i < 2 ; i++){
      if(x+size2[i] >= 0 || x+size2[i] <= 10)
	a[y][x+size2[i]] += 1;
      if(y+size2[i] >= 0 || y+size2[i] <= 10)
	a[y+size2[i]][x] += 1;
    }
  }
}
