#define N 10

void deal(int x,int y,int n,int ptnx[],int ptny[],int data[N][N]){
  int i;
  for (i=0;i<n;i++)
    if ((0<=x+ptnx[i]) && (0<=y+ptny[i]) &&
        (x+ptnx[i]<N) && (y+ptny[i]<N)){
      data[x+ptnx[i]][y+ptny[i]]++;
    }
}

