void jud(int x, int y, int size, int data[MAX][MAX]);

int main(void)
{
	int map[MAX][MAX];
	int i, j;
	int input_x, input_y, input_size;
	int max = 0;
	int zero_count = 0;

	for (i = 0; i < MAX; i++){
		for (j = 0; j < MAX; j++){
			if(i == 0 || j == 0 || i + 1 == MAX || j + 1 == MAX || i == 1 || j == 1 || i + 2 == MAX || j + 2 == MAX) map[i][j] = -1;
			else map[i][j] = 0;
		}
	}
	while (scanf("%d%*c%d%*c%d", &input_x, &input_y, &input_size) != EOF)jud(input_x, input_y, input_size, map);
	for (i = 0; i < MAX; i++){
		for (j = 0; j < MAX; j++){
			if (max < map[i][j]) max = map[i][j];
			if (!(map[i][j])) zero_count++;
		}
	}

	printf("%d\n%d\n", zero_count, max);
	return (0);
}

void jud(int x, int y, int size, int data[MAX][MAX])
{
	if (POINT(x + 1, y) != -1) POINT(x + 1, y)++;
	if (POINT(x, y) != -1) POINT(x, y)++;
	if (POINT(x - 1, y) != -1) POINT(x - 1, y)++;
	if (POINT(x, y + 1) != -1) POINT(x, y + 1)++;
	if (POINT(x, y - 1) != -1) POINT(x, y - 1)++;
	if (size > 1){
		if (POINT(x + 1, y + 1) != -1) POINT(x + 1, y + 1)++;
		if (POINT(x - 1, y + 1) != -1) POINT(x - 1, y + 1)++;
		if (POINT(x + 1, y - 1) != -1) POINT(x + 1, y - 1)++;
		if (POINT(x - 1, y - 1) != -1) POINT(x - 1, y - 1)++;
		if (size > 2){
			if (POINT(x + 2, y) != -1) POINT(x + 2, y)++;
			if (POINT(x - 2, y) != -1) POINT(x - 2, y)++;
			if (POINT(x, y + 2) != -1) POINT(x, y + 2)++;
			if (POINT(x, y - 2) != -1) POINT(x, y - 2)++;
		}
	}
}

