};

void func(int **a, int x, int y, int n, int d[4][2])
{
	int i, s, t;

	for(i = 0; i < 4; i++){
		s = x+n*d[i][0];
		t = y+n*d[i][1];
		if(s >= 0 && s <= 9 && t >= 0 && t <= 9){
			a[s][t]++;
		}
	}

	return ;
}

