public class Main {
	private static final byte MASK_RIGHT = 0x0F;//4bit
	private int[][] paper = new int[10][10];//

	public void main(String[] args) throws NumberFormatException, IOException {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 128);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	/**
	 *
	 */
	private void droppingInk(int x, int y, int size) {
		sinking(x, y - 1);
		sinking(x - 1, y);
		sinking(x, y);
		sinking(x + 1, y);
		sinking(x, y + 1);
		if (size == 1) {
			return;
		}

		sinking(x - 1, y - 1);
		sinking(x + 1, y - 1);
		sinking(x - 1, y + 1);
		sinking(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		sinking(x, y - 2);
		sinking(x - 2, y);
		sinking(x + 2, y);
		sinking(x, y + 2);
	}

	/**
	 * 
	 */
	private void sinking(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			paper[x][y]++;
		}
	}

	/**
	 *
	 */
	private void calcResult() {
		int white = 0;//
		int max = 0;//
		for (int[] row : paper) {
			for (int colum : row) {
				if (colum == 0) {
					white++;
				} else if (colum > max) {
					max = colum;
				}
			}
		}
		System.out.println(white);
		System.out.println(max);
	}
}

import java.io.BufferedReader;
import java.io.InputStreamReader;

public class Main {
	private final static byte MASK_RIGHT = 0x0F; //4bit
	private final static int STRIP_LENGTH = 10; //1
	private static int[] punchTape = new int[100]; //10*10

	public static void main(String[] args) throws Exception {
		BufferedReader reader = new BufferedReader(new InputStreamReader(System.in), 256);
		String sLine;
		while ((sLine = reader.readLine()) != null) {
			droppingInk(sLine.charAt(0) & MASK_RIGHT, sLine.charAt(2) & MASK_RIGHT, sLine.charAt(4) & MASK_RIGHT);
		}
		calcResult();
	}

	/**
	 *
	 */
	private static void droppingInk(int x, int y, int size) {
		sinking(x, y - 1);
		sinking(x - 1, y);
		sinking(x, y);
		sinking(x + 1, y);
		sinking(x, y + 1);
		if (size == 1) {
			return;
		}

		sinking(x - 1, y - 1);
		sinking(x + 1, y - 1);
		sinking(x - 1, y + 1);
		sinking(x + 1, y + 1);
		if (size == 2) {
			return;
		}

		sinking(x, y - 2);
		sinking(x - 2, y);
		sinking(x + 2, y);
		sinking(x, y + 2);
	}

	/**
	 * 
	 */
	private static void sinking(int x, int y) {
		if (x >= 0 && x < 10 && y >= 0 && y < 10) {
			punchTape[x * STRIP_LENGTH + y]++;
		}
	}

	/**
	 *
	 */
	private static void calcResult() {
		int white = 0;//
		int max = 0;//
		for (int i = 0; i < 100; i++) {
			if (punchTape[i] == 0) {
				white++;
			} else if (punchTape[i] > max) {
				max = punchTape[i];
			}
		}
		System.out.println(white);
		System.out.println(max);
	}
}

import java.io.IOException;
import java.io.BufferedReader;
import java.io.InputStreamReader;

class Main {
    static int[][] paper = new int[10][10];

    static void drop(int x, int y) {
        try {
            paper[y][x]++;
        } catch (ArrayIndexOutOfBoundsException e) {
            return;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));

        String input;

        // initialize
        for (int i = 0; i < 10; i++) {
            for (int j = 0; j < 10; j++) {
                paper[i][j] = 0;
            }
        }

        while ((input = br.readLine()) != null) {
            String[] info = input.split(",");
            int x = Integer.parseInt(info[0]);
            int y = Integer.parseInt(info[1]);

            drop(y, x);
            drop(y + 1, x);
            drop(y - 1, x);
            drop(y, x + 1);
            drop(y, x - 1);

            int size = Integer.parseInt(info[2]);

            if (size >= 2) {
                drop(y + 1, x + 1);
                drop(y + 1, x - 1);
                drop(y - 1, x + 1);
                drop(y - 1, x - 1);
            }
            if (size >= 3) {
                drop(y + 2, x);
                drop(y - 2, x);
                drop(y, x + 2);
                drop(y, x - 2);
            }

        }

        int clear = 0;
        int max = 0;

        for(int i = 0; i < 10; i++){
            for(int j = 0; j < 10; j++){
                if(paper[i][j] == 0){
                    clear++;
                }

                if(paper[i][j] > max){
                    max = paper[i][j];
                }
            }
        }

        System.out.println(clear);
        System.out.println(max);
    }
}

