				for(int j = 0; j < n; j++){
					max = Math.max(max, map[i][j]);
					if(map[i][j] == 0){
						count++;
					}
				}
			}
			System.out.printf("%d\n", count);
			System.out.printf("%d\n", max);
			scan.close();
		}
	}

	public static void drop(int xx, int yy){
		if(xx < 0 || xx >= n|| yy < 0 || yy >= n){
			return;
		}
		map[xx][yy]++;
	}
}

