	public void drop(int paper[][], int x, int y, int size) {
		if (size == 1) {
			drop1(paper, x, y);
		}
		if (size == 2) {
			drop2(paper, x, y);
		}
		if (size == 3) {
			drop3(paper, x, y);
		}
