	// 
	public void drop1(int paper[][], int x, int y) {
		paper[x][y]++;
		if (x + 1 < 10) {
			paper[x + 1][y]++;
		}
		if (x - 1 > -1) {
			paper[x - 1][y]++;
		}
		if (y + 1 < 10) {
			paper[x][y + 1]++;
		}
		if (y - 1 > -1) {
			paper[x][y - 1]++;
		}
	}

	// 
	public void drop2(int paper[][], int x, int y) {
		drop1(paper, x, y);
		if (x + 1 < 10 && y + 1 < 10) {
			paper[x + 1][y + 1]++;
		}
		if (x + 1 < 10 && y - 1 > -1) {
			paper[x + 1][y - 1]++;
		}
		if (x - 1 > -1 && y + 1 < 10) {
			paper[x - 1][y + 1]++;
		}
		if (x - 1 > -1 && y - 1 > -1) {
			paper[x - 1][y - 1]++;
		}
	}

	// 
	public void drop3(int paper[][], int x, int y) {
		drop2(paper, x, y);
		if (x + 2 < 10) {
			paper[x + 2][y]++;
		}
		if (x - 2 > -1) {
			paper[x - 2][y]++;
		}
		if (y + 2 < 10) {
			paper[x][y + 2]++;
		}
		if (y - 2 > -1) {
			paper[x][y - 2]++;
		}
	}
}

