		Scanner sc = new Scanner(System.in);

		Map<String, Integer> dropingMap = new HashMap<String, Integer>();
		for (int x = 0; x < 10; x++) {
			for (int y = 0; y < 10; y++) {
				dropingMap.put(String.valueOf(x).concat(String.valueOf(y)), 0);

			}
		}
		while (sc.hasNext()) {
			String[] s = sc.next().split(",");
			int dx = Integer.parseInt(s[0]);
			int dy = Integer.parseInt(s[1]);
			int d = Integer.parseInt(s[2]);

			if (d == 1) {

				dropMin(dx, dy, dropingMap);

			} else if (d == 2) {

				dropMid(dx, dy, dropingMap);

			} else {
				dropMax(dx, dy, dropingMap);
			}

			checkMax(dropingMap);
		}

	}

	static void checkMax(Map<String, Integer> dropingMap) {

		int maxVal = 0;
		int coutZero = 0;
		for (Iterator dropMap = dropingMap.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			int value = (Integer) entry.getValue();

			if (maxVal < value) {
				maxVal = value;
			}
			if (value == 0) {
				coutZero = coutZero + 1;
			}
		}
		System.out.println(coutZero);
		System.out.println(maxVal);

	}

	//String1x 2y
	static void dropMin(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);
		}
	}

	//String1x 2y
	static void dropMid(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//+
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);
			refreshMap(x + 1, y + 1, entry, key);
			refreshMap(x - 1, y - 1, entry, key);
			refreshMap(x + 1, y - 1, entry, key);
			refreshMap(x - 1, y + 1, entry, key);
		}

	}

	//String1x 2y
	static void dropMax(int x, int y, Map<String, Integer> dropMapData) {

		for (Iterator dropMap = dropMapData.entrySet().iterator(); dropMap.hasNext();) {
			Map.Entry entry = (Map.Entry) dropMap.next();
			String key = (String) entry.getKey();
			refreshMap(x, y, entry, key);

			//+
			refreshMap(x, y + 1, entry, key);
			refreshMap(x, y - 1, entry, key);
			refreshMap(x + 1, y, entry, key);
			refreshMap(x - 1, y, entry, key);

			refreshMap(x, y + 2, entry, key);
			refreshMap(x, y - 2, entry, key);
			refreshMap(x + 2, y, entry, key);
			refreshMap(x - 2, y, entry, key);

			refreshMap(x + 1, y + 1, entry, key);
			refreshMap(x - 1, y - 1, entry, key);
			refreshMap(x + 1, y - 1, entry, key);
			refreshMap(x - 1, y + 1, entry, key);
		}

	}

	static void refreshMap(int x, int y, Map.Entry entry, String key) {

		if (-1 < x && x < 10 && -1 < y && y < 10 && key.equals((String.valueOf(x)).concat(String.valueOf(y)))) {
			entry.setValue((Integer) entry.getValue() + 1);
		}
	}
}

import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStream;
import java.io.InputStreamReader;
import java.io.PrintStream;

class Main {

    public static void main(String[] args) throws IOException {
        doit(args, System.in, System.out);
    }

    static void doit(String[] args, InputStream in, PrintStream out)
            throws IOException {
        BufferedReader reader = new BufferedReader(new InputStreamReader(in));
        String line = null;
        int[][] counts = new int[14][14];
        while ((line = reader.readLine()) != null && !line.isEmpty()) {
            String[] values = line.split(",");
            int x = Integer.parseInt(values[0]);
            int y = Integer.parseInt(values[1]);
            int size = Integer.parseInt(values[2]);
            int i = x + 2;
            int j = y + 2;
            counts[i][j]++;
            counts[i][j - 1]++;
            counts[i + 1][j]++;
            counts[i][j + 1]++;
            counts[i - 1][j]++;
            if (size >= 2) {
                counts[i + 1][j - 1]++;
                counts[i + 1][j + 1]++;
                counts[i - 1][j + 1]++;
                counts[i - 1][j - 1]++;
                if (size >= 3) {
                    counts[i][j - 2]++;
                    counts[i + 2][j]++;
                    counts[i][j + 2]++;
                    counts[i - 2][j]++;
                }
            }
        }
        int emptyCount = 0;
        int maxCount = 0;
        for (int i = 2; i < 12; i++) {
            for (int j = 2; j < 12; j++) {
                int count = counts[i][j];
                if (count == 0) {
                    emptyCount++;
                } else if (count > maxCount) {
                    maxCount++;
                }
            }
        }
        out.println(emptyCount);
        out.println(maxCount);
    }
}

import java.util.Scanner;

public class Main {

	private static int[][] board = new int[10][10];

	private static final int SMALL  = 1,
			                 MEDIUM = 2,
			                 LARGE  = 3;


    public static void main(String[] args) {

		for(int i=0;i<10;i++){
			for(int j=0;j<10;j++){
				board[i][j] = 0;
			}
		}

        Scanner sc = new Scanner(System.in).useDelimiter(System.getProperty("line.separator"));

        String[] s;
        while(sc.hasNext()){
        	s = sc.next().split(",");
        	update_board(Integer.parseInt(s[0]),Integer.parseInt(s[1]),Integer.parseInt(s[2]));
        }
        System.out.println(cnt_0_cell());
        System.out.println(max_cell());
    }

    private static void update_board(int x,int y,int size){

		for(int i=0;i<10;i++){
			for(int j=0;j<10;j++){
				if((((i == x && (j <= y + 1 &&  j >= y - 1)) || ((i <= x + 1 &&  i >= x - 1) && j == y)) && size == SMALL)  ||
				  (              j <= y + 1 &&  j >= y - 1   &&   i <= x + 1 &&  i >= x - 1              && size == MEDIUM) ||
				  ((             j <= y + 1 &&  j >= y - 1   &&   i <= x + 1 &&  i >= x - 1            ) ||
					            (i == x     &&  j == y - 2)  || ( i == x     &&  j == y + 2)             ||
					            (i == x - 2 &&  j == y    )  || ( i == x + 2 &&  j == y    )   		   ) && size == LARGE)
					{board[i][j]++;}
			}
		}
    }

    private static int cnt_0_cell(){
    	int sum = 0;
		for(int i=0;i<10;i++){
			for(int j=0;j<10;j++){
				if(board[i][j] == 0){sum++;}
			}
		}
		return sum;
    }

    private static int max_cell(){
    	int max = 0;
		for(int i=0;i<10;i++){
			for(int j=0;j<10;j++){
				if(max < board[i][j]){max = board[i][j];}
			}
		}
		return max;
    }

}

