import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;

public class Main {

	// 
	private int[][] cell = new int[10][10];

	public static void main(String[] args) throws Exception {
		new Main().execute();
	}

	/**
	 * 
	 */
	private void execute() throws IOException {

		// 
		BufferedReader reader = new BufferedReader(new InputStreamReader(
				System.in));

		// 
		String line = null;
		while ((line = reader.readLine()) != null) {
			String[] inkInfo = line.split(",");

			// 
			calcInkDropPoint(Integer.parseInt(inkInfo[0]),
					Integer.parseInt(inkInfo[1]), Integer.parseInt(inkInfo[2]));
		}

		// 
		outputInkDropPoint();

	}

	/**
	 * 
	 * 
	 * @param x
	 *            X
	 * @param y
	 *            Y
	 * @param size
	 *            
	 */
	private void calcInkDropPoint(int x, int y, int size) {

		// 
		cell[x][y] += 1;

		// 
		addPoint(x + 1, y);
		addPoint(x - 1, y);
		addPoint(x, y + 1);
		addPoint(x, y - 1);

		// 
		if (size > 1) {
			addPoint(x + 1, y + 1);
			addPoint(x - 1, y + 1);
			addPoint(x + 1, y - 1);
			addPoint(x - 1, y - 1);
		}

		// 
		if (size > 2) {
			addPoint(x + 2, y);
			addPoint(x - 2, y);
			addPoint(x, y + 2);
			addPoint(x, y - 2);
		}

	}

	/**
	 *  
	 */
	private void outputInkDropPoint() {

		int totalCell = 0;
		int maxPoint = 0;

		for (int i = 0; i < cell.length; i++) {
			for (int j = 0; j < cell[i].length; j++) {

				// 
				if (cell[i][j] == 0) {
					totalCell += 1;
				}

				// 
				if (cell[i][j] > maxPoint) {
					maxPoint = cell[i][j];
				}
			}
		}

		// 
		System.out.println(totalCell);
		System.out.println(maxPoint);
	}

	/**
	 * 
	 * 
	 * @param direction
	 *            
	 * @return (true:false:)
	 */
	private boolean isInsideOfCell(int direction) {
		return (direction >= 0) && (direction < 10);
	}

	/**
	 * 
	 * 
	 * @param x
	 *            X
	 * @param y
	 *            Y
	 */
	private void addPoint(int x, int y) {
		// 
		if (isInsideOfCell(x) && isInsideOfCell(y)) {
			cell[x][y] += 1;
		}
	}

}

