		while (str != null) {
			// (","),
			String[] strList = str.split(",");
			int x = Integer.parseInt(strList[0]);
			int y = Integer.parseInt(strList[1]);
			int size = Integer.parseInt(strList[2]);

			// 
			Main main = new Main();
			main.drop(paper, x, y, size);
		}

		// 1
		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				if (paper[i][j] == 0) {
					emptySpace += 1;
				}
				if (paper[i][j] > max) {
					max = paper[i][j];
				}
			}
		}

		System.out.println(emptySpace);
		System.out.println(max);
	}

	// 
	public void drop(int paper[][], int x, int y, int size) {
		if (size == 1) {
			drop1(paper, x, y);
		}
		if (size == 2) {
			drop2(paper, x, y);
		}
		if (size == 3) {
			drop3(paper, x, y);
		}
	}

	// 
	public void drop1(int paper[][], int x, int y) {
		paper[x][y] += 1;
		if (x + 1 < 10) {
			paper[x + 1][y] += 1;
		}
		if (x - 1 > -1) {
			paper[x - 1][y] += 1;
		}
		if (y + 1 < 10) {
			paper[x][y + 1] += 1;
		}
		if (y - 1 > -1) {
			paper[x][y - 1] += 1;
		}
	}

	// 
	public void drop2(int paper[][], int x, int y) {
		drop1(paper, x, y);
		if (x + 1 < 10 && y + 1 < 10) {
			paper[x + 1][y + 1] += 1;
		}
		if (x + 1 < 10 && y - 1 > -1) {
			paper[x + 1][y - 1] += 1;
		}
		if (x - 1 > -1 && y + 1 < 10) {
			paper[x - 1][y + 1] += 1;
		}
		if (x - 1 > -1 && y - 1 > -1) {
			paper[x - 1][y - 1] += 1;
		}
	}

	// 
	public void drop3(int paper[][], int x, int y) {
		drop2(paper, x, y);

		if (x + 2 < 10) {
			paper[x + 2][y] += 1;
		}
		if (x - 2 > -1) {
			paper[x - 2][y] += 1;
		}
		if (y + 2 < 10) {
			paper[x][y + 2] += 1;
		}
		if (y - 2 > -1) {
			paper[x][y - 2] += 1;
		}

	}

}

