					cnt++;
				}
				if(max <= masu[i][j]){
					max = masu[i][j];
				}
			}
		}

		System.out.println(cnt + "\n" + max);

	}

	static void drop(int brocks[][],int x,int y,int s){
		if(s == 1){
			min_drop(brocks,x,y);
		}
		if(s == 2){
			mid_drop(brocks,x,y);
		}
		if(s == 3){
			lag_drop(brocks,x,y);
		}
	}
	static void min_drop(int brocks[][],int x,int y){
		brocks[x][y]++;
		if(x >= 0){
			brocks[x-1][y] += 1;
		}
		if(x <= 9){
			brocks[x+1][y] += 1;
		}
		if(y >= 0){
			brocks[x][y-1] += 1;
		}
		if(y <= 9){
			brocks[x][y+1] += 1;
		}

	}

	static void mid_drop(int brocks[][],int x,int y){
		min_drop(brocks,x,y);
		if(x >= 0 && y >= 0){
			brocks[x-1][y-1]++;
		}
		if(x <= 9 && y >= 0){
			brocks[x+1][y-1]++;
		}
		if(x >= 0 && y <=9){
			brocks[x-1][y+1]++;
		}
		if(x <= 9 && y<= 9){
			brocks[x+1][y+1]++;
		}

	}

	static void lag_drop(int brocks[][],int x,int y){
		mid_drop(brocks,x,y);
		if(x >= 1){
			brocks[x-2][y]++;
		}
		if(x <= 8){
			brocks[x+2][y]++;
		}
		if(y >= 1){
			brocks[x][y-2]++;
		}
		if(y <= 8){
			brocks[x][y+2]++;
		}
	}

}

import java.util.*;

public class Main {

    public static void main (String[] args) {
        Scanner scanner = new Scanner(System.in);
        int[][] paper = new int[10][10];
        int max = 0;
        int counter = 0;

        for (int i = 0; i < paper.length; i++) {
            for (int j = 0; j < paper[i].length; j++) {
                paper[i][j] = 0;
            }
        }

        while (scanner.hasNext()) {
            String strLine = scanner.nextLine();
            String[] str = strLine.split(",");

            int x = Integer.parseInt(str[0]);
            int y = Integer.parseInt(str[1]);
            int size = Integer.parseInt(str[2]);

            paper[x][y]++;
            if (isInsidePaper(x, y-1)) paper[x][y-1]++;
            if (isInsidePaper(x-1, y)) paper[x-1][y]++;
            if (isInsidePaper(x+1, y)) paper[x+1][y]++;
            if (isInsidePaper(x, y+1)) paper[x][y+1]++;

            if (size >= 2) {
                if (isInsidePaper(x-1, y-1))    paper[x-1][y-1]++;
                if (isInsidePaper(x+1, y-1))    paper[x+1][y-1]++;
                if (isInsidePaper(x-1, y+1))    paper[x-1][y+1]++;
                if (isInsidePaper(x+1, y+1))    paper[x+1][y+1]++;
            }
            if (size == 3) {
                if (isInsidePaper(x, y-2))    paper[x][y-2]++;
                if (isInsidePaper(x-2, y))    paper[x-2][y]++;
                if (isInsidePaper(x+2, y))    paper[x+2][y]++;
                if (isInsidePaper(x, y+2))    paper[x][y+2]++;
            }
        }
        for (int i = 0; i < paper.length; i++) {
            for (int j = 0; j < paper[i].length; j++) {
                if (paper[i][j] == 0) {
                    counter++;
                } else {
                    if (paper[i][j] > max) max = paper[i][j];
                }
            }
        }
        System.out.println(counter);
        System.out.println(max);
    }

    public static boolean isInsidePaper(int x, int y) {
        if (x >= 0 && x <= 9 && y >= 0 && y <= 9) {
            return true;
        } else {
            return false;
        }
    }
}

