		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				paper[i][j] = 0;
			}
		}
		while (sc.hasNext()) {
			int x = sc.nextInt();
			int y = sc.nextInt();
			int size = sc.nextInt();
			switch (size) {
			case 1:
				drawsmall(x,y);
				break;
			case 2:
				drawmid(x,y);
				break;
			case 3:
				drawlarge(x,y);
				break;
			}
		}
		for(int k = 0; k < 10;k++){
			for(int l = 0;l < 10;l++){
				if(paper[k][l] == 0){
					zerocount++;
