import java.util.Arrays;
import java.util.Scanner;

public class Main {
	private int[][] paper = new int[10][10];

	public static void main(String[] args) {
		new Main().run();
	}

	private void run() {
		Scanner scan = new Scanner(System.in);

		for (int i = 0; i < 10; i++) Arrays.fill(paper[i], 0);

		while (scan.hasNext()) {
			String str = scan.nextLine();
			int x = Integer.parseInt(str.substring(0, 1));
			int y = Integer.parseInt(str.substring(2, 3));
			int s = Integer.parseInt(str.substring(4, 5));

			try {
				if (s == 3) {
					large(x,y);
				}else if (s == 2) {
					medium(x, y);
				}else {
					small(x, y);
				}
			}catch (ArrayIndexOutOfBoundsException e) {

			}
		}
		int count = 0, max = -1;
		for (int i = 0; i < 10; i++) {
			for (int j = 0; j < 10; j++) {
				if (paper[i][j] == 0) count++;
				max = Math.max(max, paper[i][j]);
			}
		}
		System.out.println(count);
		System.out.println(max);
	}

	private void large(int x, int y) {
		medium(x, y);
		paper[x][y - 2]++;
		paper[x][y + 2]++;
		paper[x - 2][y]++;
		paper[x + 2][y]++;
	}

	private void medium(int x, int y) {
		small(x, y);
		paper[x - 1][y - 1]++;
		paper[x - 1][y + 1]++;
		paper[x + 1][y - 1]++;
		paper[x + 1][y + 1]++;
	}

	private void small(int x, int y) {
		paper[x][y - 1]++;
		paper[x][y + 1]++;
		paper[x - 1][y]++;
		paper[x + 1][y]++;
	}

	private void debug() {
		System.out.println();
	}

}

import java.io.IOException;
import java.util.*;

/**
 */
class Main {
	final static int N = 10;
	public static void main(String[] args) throws IOException {
		Scanner in = new Scanner(System.in);

		int[][] paper = new int[N][N];
		while(in.hasNext()) {
			String str = in.nextLine();
			String[] split = str.split(",");
			int x = Integer.parseInt(""+split[0]);
			int y = Integer.parseInt(""+split[1]);
			int s = Integer.parseInt(""+split[2]);

			switch (s) {
				case 1:
					fillSmall(paper, x, y);
					break;
				case 2:
					fillMid(paper, x, y);
					break;
				case 3:
					fillLarge(paper, x, y);
					break;
				default:
					if (!false) throw new AssertionError();
			}
		}

		int numOfZero = 0;
		int maxVal = 0;
		for(int i=0;i<N;i++) {
			for(int j=0;j<N;j++) {
				if(paper[i][j] == 0) {
					numOfZero++;
				}
				maxVal = Math.max(maxVal, paper[i][j]);
			}
		}
		System.out.println(numOfZero);
		System.out.println(maxVal);
	}

	public static boolean checkOk(int x, int y) {
		return (0 <= x && x < N && 0 <= y && y < N);
	}

	public static void fillSmall(int[][] paper, int cx, int cy) {
		int[] tx = {0,-1,0,1,0};
		int[] ty = {-1,0,0,0,1};
		for(int i=0;i<5;i++) {
			int x = cx + tx[i];
			int y = cy + ty[i];
			if(checkOk(x, y) == false) continue;
			paper[x][y] += 1;
		}
	}

	public static void fillMid(int[][] paper, int cx, int cy) {
		for(int i=0;i<3;i++) {
			for(int j=0;j<3;j++) {
				int x = (cx - 1) + j;
				int y = (cy - 1) + i;
				if(checkOk(x,y) == false) continue;
				paper[x][y] += 1;
			}
		}
	}

	public static void fillLarge(int[][] paper, int cx, int cy) {
		int[] tx =
			{0,
				-1,0,1,
				-2,-1,0,1,2,
				-1,0,1,
				0};
		int[] ty =
				{
						-2,
						-1,-1,-1,
						0,0,0,0,0,
						1,1,1,
						2
				};
		for(int i=0;i<13;i++) {
			int x = cx + tx[i];
			int y = cy + ty[i];
			if(checkOk(x,y) == false) continue;
			paper[x][y] += 1;
		}
	}
}

